import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React from 'react'

//seperated purely for maintainability

const CATEGORIES = [
  {
    name: "Food & Drink" ,
    icon: faBowlFood
  },
  {
    name: "Days Out" ,
    icon: faSun
  },

  {
    name: "Travel & Accomodation" ,
    icon: faPlaneDeparture
  },

  {
    name: "Leisure & Entertainment" ,
    icon: faMasksTheater
  },

  {
    name: "Clothing" ,
    icon: faShirt
