  {
    name: "Fashion Accessories" ,
    icon: faShoppingBag
  },

  {
    name: "Health & Beauty" ,
    icon: faHeart
  },

  {
    name: "Electricals" ,
    icon: faPlug
  },

  {
    name: "Home & Garden" ,
    icon: faHouse
  },

  {
    name: "Gifts" ,
    icon: faGift
  },

  {
    name: "Automotive" ,
    icon: faCar
  },

  {
    name: "Education" ,
    icon: faSchool
  },

  {
    name: "Baby & Child" ,
    icon: faChildReaching
  },

  {
    name: "Finance" ,
    icon: faMoneyCheckDollar
  },


]

const OfferCategories = () => {

  return (
    <>
      <h2>Offer Categories</h2>
      <div className='row m-5 bg-white rounded'>

        {CATEGORIES.map((c,i) =>{
          return <div class="col-xl-3 col-lg-4 col-md-4 col-sm-6 col-12 d-flex "><a href={`/categories/${c.name}`}><FontAwesomeIcon icon={c.icon}/> {c.name}</a></div>
        })}

      </div>
    </>
  )
}

export default OfferCategories
