import React, { useEffect } from 'react'
import { tns } from "../../node_modules/tiny-slider/src/tiny-slider"


const Tslider = () => {
    useEffect(() => {
        tns({

            container: '.my-slider',
            items: 6,
            slideBy: 'page',
            autoplay: true,
            "swipeAngle": false,

        })
    },[])

    return (
    <div className="my-slider">
        <div className='container mx-5'>1</div>
        <div className='container mx-5'>2</div>
        <div className='container mx-5'>3</div>
        <div className='container mx-5'>4</div>
        <div className='container mx-5'>5</div>
        <div className='container mx-5'>6</div>
    </div>
  )
}

export default Tslider
