import React, {useEffect, useState} from 'react'
import ImgCard from './Cards/ImgCard'
import OfferCard from './Cards/OfferCard'
import CardsContainer from './CardsContainer'
import Carousel from './Carousel'

const Homepage = () => {

  const [data,setData] = useState([])

  useEffect(() => {
    const arr = new Array(12)
    for(let i = 0; i< arr.length; i++){
      arr[i] = 
      {
        primaryImg: "https://www.shutterstock.com/image-vector/woman-man-clothes-accessories-collection-260nw-1488991403.jpg",
        secondaryImg: "https://1000logos.net/wp-content/uploads/2020/07/Asos-Logo.jpg",
        description:"Use this exclusive code and take 10% off and free delivery!",
        discountCode:"code",
        linkUrl:"asdsa",
        buttonText: "View Offer",
      }
    }
    console.log(arr)
    setData(arr)
  }, [])


  return (
    <div className='container-fluid flex flex-col'>
      <div className='container-fluid d-flex justify-content-center p-5'>
        <h1>Logo</h1>
      </div>

      <div className='container-fluid d-flex flex-column align-items-center  bg-light'>
        <div className='container-fluid d-flex justify-content-center'>
          <h2 className='my-5'>Top Offers</h2>
        </div>

        <CardsContainer offers={data}/>
        <Carousel/>  
        <OfferCard height={500} width={300} primaryImg="https://www.shutterstock.com/image-vector/woman-man-clothes-accessories-collection-260nw-1488991403.jpg" secondaryImg={"https://1000logos.net/wp-content/uploads/2020/07/Asos-Logo.jpg"} description={"description"}/>
      </div>


    </div>
  )
}

export default Homepage
