import React, {useEffect, useState} from 'react'
import ImgCard from './Cards/ImgCard'
import CardsContainer from './CardsContainer'
import Carousel from './Carousel'

const Homepage = () => {

  const [data,setData] = useState([])

  useEffect(() => {
    const arr = new Array(12)
    for(let i = 0; i< arr.length; i++){
      arr[i] = 
      {
        primaryImg: "https://media.istockphoto.com/id/1265900812/photo/aerial-view-of-london-and-the-tower-bridge.jpg?s=612x612&w=0&k=20&c=-2j6QgZyeveMcGzuWmgLYqC7zXTkcSBnayuZsDqs5KQ=",
        secondaryImg: "https://logos-world.net/wp-content/uploads/2022/04/Tesco-Logo.png",
        text:"hello there",
        discountCode:"code",
        linkUrl:"asdsa"
      }
    }
    console.log(arr)
    setData(arr)
  }, [])


  return (
    <div className='container-fluid flex flex-col'>
      <div className='container-fluid d-flex justify-content-center p-5'>
        <h1>Logo</h1>
      </div>

      <div className='container-fluid d-flex flex-column align-items-center  bg-light'>
        <div className='container-fluid d-flex justify-content-center'>
          <h2 className='my-5'>Top Offers</h2>
        </div>

        <CardsContainer offers={data}/>
      </div>


    </div>
  )
}

export default Homepage
