import { tns } from "../../node_modules/tiny-slider/src/tiny-slider"


const Tslider = () => {
    useEffect(() => {
        tns({
            container: '.slider-list',
            items: 1,
            slideBy: 'page',
            autoplay: true,
        })
    },[])

    return (
    <div className="my-slider">
        <div>1</div>
        <div>2</div>
        <div>3</div>
        <div>4</div>
        <div>5</div>
        <div>6</div>
    </div>
  )
}

export default Tslider
