import React from 'react'

import { faBowlFood, faPlaneDeparture, faMasksTheater, faShirt, faHeart, faPlug, faHouse, faCar, faSchool, faChildReaching, faMoneyCheckDollar, faGift } from '@fortawesome/free-solid-svg-icons'

const CATEGORIES = [
    {
      name: "Food & Drink" ,
      icon: faBowlFood
    },


    {
      name: "Travel & Accomodation" ,
      icon: faPlaneDeparture
    },

    {
      name: "Leisure & Entertainment" ,
      icon: faMasksTheater
    },

    {
      name: "Clothing & Fashion" ,
      icon: faShirt
    },


    {
      name: "Health & Beauty" ,
      icon: faHeart
    },

    {
      name: "Electronics" ,
      icon: faPlug
    },

    {
      name: "Home & Garden" ,
      icon: faHouse
    },

    {
      name: "Gifts" ,
      icon: faGift
    },

    {
      name: "Automotive" ,
      icon: faCar
    },

    {
      name: "Education" ,
      icon: faSchool
    },

    {
      name: "Baby & Child" ,
      icon: faChildReaching
    },

    {
      name: "Finance" ,
      icon: faMoneyCheckDollar
    },


  ]

const CategoriesPage = () => {
  return (
    <div className="container-fluid bg-light-grey p-5 d-flex align-items-center flex-column">
        <h2>Offer Categories</h2>

        <div className='container row bg-grey rounded pl-5 pt-4 pb-4'>
        {CATEGORIES.map((c,i) =>{
          return <div className="col-xl-3 col-lg-4 col-md-6 col-sm-6 col-12 d-flex my-3 grow">
            <strong><a className='link-dark' id="xxx" href={`/categories/${c.name}`} ><FontAwesomeIcon icon={c.icon} className="fa-icon-category text-warning"/> {c.name}</a>
            </strong>
            </div>
        })}
        </div>

    </div>
  )
}

export default CategoriesPage
